import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cartIsVisible: false,
};

const uiSlice = createSlice({
  name: "ui",
  initialState: {
    cardIsVidible: false,
  },
  reducers: {
    toggle(state) {
      state.cardIsVidible = !state.cardIsVidible;
    },
  },
});

export const uiActions = uiSlice.actions;

export default uiSlice.reducer;
